import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
interface HotelSearchResponse {
  // Define the structure of your hotel search response
  // You may need to adjust this based on the actual response from your backend
  // For now, assuming it's a string
  result: string;
}
@Component({
  selector: 'app-hotel',
  templateUrl: './hotel.component.html',
  styleUrls: ['./hotel.component.css']
})
export class HotelComponent {

  searchModel = {
    arrivalDate: '',
    departureDate: '',
    adults: 1,
    roomQty: 1
  };

  // Result from the backend
  searchResult: string = '';

  // Inject HttpClient for making HTTP requests
  constructor(private http: HttpClient) {}

  // Function to handle form submission
  onSubmit(): void {
    // Assuming your backend endpoint is at http://localhost:8080/hotels/search
    const apiUrl = 'http://localhost:8080/hotels/search';

    // Make the HTTP request
    this.http.get<HotelSearchResponse>(apiUrl, {
      params: {
        arrivalDate: this.searchModel.arrivalDate,
        departureDate: this.searchModel.departureDate,
        adults: this.searchModel.adults.toString(),
        room_qty: this.searchModel.roomQty.toString()
      }
    }).subscribe(response => {
      // Handle the response from the backend
      this.searchResult = response.result;
    });
  }
}

<div>
  <h2>Hotel Search</h2>
  <form (ngSubmit)="onSubmit()">
    <label for="arrivalDate">Arrival Date:</label>
    <input [(ngModel)]="searchForm.arrivalDate" name="arrivalDate" />
    <input [(ngModel)]="searchForm.departureDate" name="departureDate" />
    <!-- Add name attributes for other input fields as well -->

    <label for="departureDate">Departure Date:</label>
    <input type="date" id="departureDate" [(ngModel)]="searchModel.departureDate" required>

    <label for="adults">Adults:</label>
    <input type="number" id="adults" [(ngModel)]="searchModel.adults" required>

    <label for="roomQty">Room Quantity:</label>
    <input type="number" id="roomQty" [(ngModel)]="searchModel.roomQty" required>

    <button type="submit">Search Hotels</button>
  </form>

  <div *ngIf="searchResult">
    <h3>Search Result</h3>
    <p>{{ searchResult }}</p>
  </div>
</div>
