import { Component ,Input, OnInit} from '@angular/core';

@Component({
  selector: 'app-hotel-search-results',
  templateUrl: './hotel-search-results.component.html',
  styleUrls: ['./hotel-search-results.component.css']
})
export class HotelSearchResultsComponent implements OnInit {
  @Input() hotelsData: any;

  hotels: any[] = [];

  ngOnInit(): void {
    if (this.hotelsData && this.hotelsData.data && this.hotelsData.data.hotels) {
      this.hotels = this.hotelsData.data.hotels;
      console.log('Result of hotels Data received:', this.hotels);
    }
  }
}
<div>
  <h2>Hotel Search Result</h2>

  <div *ngIf="resultData && resultData.data && resultData.data.hotels && resultData.data.hotels.length > 0">
    <div *ngFor="let hotel of resultData.data.hotels" class="hotel-item">
      <img [src]="hotel.property.photoUrls[0]" alt="Hotel Image">
      <h4>{{ hotel.property.name }}</h4>
      <p>Rating: {{ hotel.property.reviewScore }} ({{ hotel.property.reviewScoreWord }})</p>
      <p>Price: {{ hotel.property.priceBreakdown.grossPrice.value }} {{ hotel.property.priceBreakdown.grossPrice.currency }}</p>
      <p>{{ hotel.accessibilityLabel }}</p>
    </div>
  </div>

  <div *ngIf="!hotelsData || !resultData.data || !resultData.data.hotels || resultData.data.hotels.length === 0">
    <p>No hotel data available</p>
  </div>
</div>
