import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
interface HotelSearchResponse {
  // Define the structure of your hotel search response
  // You may need to adjust this based on the actual response from your backend
  // For now, assuming it's a string
  result: string;
}
@Component({
  selector: 'app-hotel',
  templateUrl: './hotel.component.html',
  styleUrls: ['./hotel.component.css']
})
export class HotelComponent {

  searchForm: any = {}; // This object will hold the form data
  searchResult: any;
  constructor(private http: HttpClient) {}


  // Function to handle form submission

  searchHotels() {
    const url = `http://localhost:8089/hotels/search`;
    const queryParams = `?arrivalDate=${this.searchForm.arrivalDate}&departureDate=${this.searchForm.departureDate}&adults=${this.searchForm.adults}&roomQty=${this.searchForm.roomQty}`;

    this.http.get<any>(url + queryParams).subscribe(
      (response) => {
        console.log('Hotel search result*************:', response);
        this.searchResult = response; // Assign the response to the searchResult property
      },
      (error) => {
        console.error('Error occurred:', error);
        // Handle error responses
      }
    );
  }
}

<div>
  <h2>Hotel Search</h2>

  <form (ngSubmit)="searchHotels()" #hotelForm="ngForm">
    <label for="arrivalDate">Arrival Date:</label>
    <input type="date" id="arrivalDate" name="arrivalDate" [(ngModel)]="searchForm.arrivalDate" required>

    <label for="departureDate">Departure Date:</label>
    <input type="date" id="departureDate" name="departureDate" [(ngModel)]="searchForm.departureDate" required>

    <label for="adults">Adults:</label>
    <input type="number" id="adults" name="adults" [(ngModel)]="searchForm.adults" required>

    <label for="roomQty">Room Quantity:</label>
    <input type="number" id="roomQty" name="roomQty" [(ngModel)]="searchForm.roomQty" required>

    <button type="submit" [disabled]="hotelForm.invalid">Search Hotels</button>
  </form>

  <!-- Display the result here -->
  <div *ngIf="searchResult">
    <h3>Search Result:</h3>
    <pre>{{ searchResult | json }}</pre>
  </div>
</div>
