import { Component ,Input} from '@angular/core';

@Component({
  selector: 'app-hotel-search-results',
  templateUrl: './hotel-search-results.component.html',
  styleUrls: ['./hotel-search-results.component.css']
})
export class HotelSearchResultsComponent {
  @Input() hotels!: any[];

}
<div *ngIf="hotels && hotels.length > 0">
  <h3>Hotel Search Results:</h3>
  <div *ngFor="let hotel of hotels" class="hotel-item">
    <img [src]="hotel.property.photoUrls[0]" alt="Hotel Image">
    <h4>{{ hotel.property.name }}</h4>
    <p>Rating: {{ hotel.property.reviewScore }} ({{ hotel.property.reviewScoreWord }})</p>
    <p>Price: {{ hotel.property.priceBreakdown.grossPrice.value }} {{ hotel.property.priceBreakdown.grossPrice.currency }}</p>
    <p>{{ hotel.accessibilityLabel }}</p>
  </div>
</div>
